import { Component } from '@angular/core';
import { ServiceModuleService } from 'src/app/services/service-module.service'; 

@Component({
  selector: 'app-module',
  templateUrl: './module.component.html',
  styleUrls: ['./module.component.css']
})
export class ModuleComponent {
  constructor(private serviceModule: ServiceModuleService){}
  module = {
    nameModule: '',
    credit: ''
   
  };
  addModule() : void {
    
      this.serviceModule.createModule(this.module).subscribe(response => {
        console.log('User registered successfully', response);
      }, error => {
        console.error('There was an error!', error);
      });
    }

}
<!DOCTYPE html>
<html lang="fr">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css">
  <style>
    body {
      margin: 0;
      font-family: Arial, sans-serif;
      background: #f0f0f0;
    }
    .form-container {
      display: flex;
      justify-content: center;
      align-items: center;
      height: 100vh;
    }
    .form-box {
      background: #fff;
      padding: 30px;
      border-radius: 5px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
      width: 50%;
    }
    .form-group {
      margin-bottom: 20px;
    }
    .form-group label {
      display: block;
      margin-bottom: 5px;
      color: #26a9e0;
      font-weight: bold;
    }
    .form-control {
      width: 100%;
      padding: 10px;
      border: 1px solid #ccc;
      border-radius: 5px;
    }
    .btn-primary {
      padding: 10px 20px;
      border: none;
      background: #26a9e0;
      color: #fff;
      font-size: 15px;
      font-weight: 400;
      border-radius: 5px;
      cursor: pointer;
      display: block;
      width: 100%;
      margin-top: 20px;
    }
    .btn-primary:hover {
      background: #85d6de;
    }
    @media (max-width: 768px) {
      .form-box {
        width: 80%;
      }
    }
  </style>
</head>
<body>
<div class="form-container">
  <div class="form-box">
    <form #AddForm="ngForm" (ngSubmit)="addModule()">
      <div class="form-group">
        <label for="nameModule">Module Name</label>
        <input type="text" class="form-control" id="nameModule" [(ngModel)]="module.nameModule" name="nameModule">
      </div>
      <div class="form-group">
        <label for="credit">Credit</label>
        <input type="text" class="form-control" id="credit" [(ngModel)]="module.credit" name="credit">
      </div>
      <button type="submit" class="btn btn-primary">Add Module</button>
    </form>
  </div>
</div>
</body>
</html>
