import { Component } from '@angular/core';
import { ServiceModuleService } from 'src/app/services/service-module.service'; 

@Component({
  selector: 'app-module',
  templateUrl: './module.component.html',
  styleUrls: ['./module.component.css']
})
export class ModuleComponent {
  
  
  constructor(private serviceModule: ServiceModuleService){}
  module = {
    nameModule: '',
    credit: ''
   
  };
  addModule() : void {
    
      this.serviceModule.createModule(this.module).subscribe(response => {
        console.log('Module Added successfully', response);
      }, error => {
        console.error('There was an error!', error);
      });
    }

    deleteModule(moduleId: number): void {
      this.serviceModule.deleteModule(moduleId).subscribe(response => {
        console.log('Module Deleted successfully', response);
      }, error => {
        console.error('There was an error!', error);
      });
    }

    updateModule(moduleId: number, newData: any): void {
      this.serviceModule.updateModule(moduleId, newData).subscribe(response => {
        console.log('Module Updated successfully', response);
      }, error => {
        console.error('There was an error!', error);
      });
    }
  
    getAllModules(): void {
      this.serviceModule.getModules().subscribe(modules => {
        console.log('List of Modules:', modules);
      }, error => {
        console.error('There was an error!', error);
      });
    }
  
    getModuleById(moduleId: number): void {
      this.serviceModule.getModuleById(moduleId).subscribe(module => {
        console.log('Module Details:', module);
      }, error => {
        console.error('There was an error!', error);
      });
    }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css">
  <style>
    /* Votre CSS existant ici */
  </style>
</head>
<body>
<div class="main-block">
  <div class="form-block">
    <i class="fas fa-pencil-alt"></i>
    <h1>Add Module</h1>
    <!-- Formulaire d'ajout de module -->
    <form #AddForm="ngForm" (ngSubmit)="addModule()">
      <!-- Vos champs de formulaire existants ici -->
      <button type="submit" class="btn btn-primary" [disabled]="!AddForm.form.valid">Add Module</button>
    </form>
  </div>
</div>

<!-- Modal pour l'affichage complet -->
<div id="myModal" class="modal">
  <div class="modal-content">
    <span class="close" (click)="closeModal()">&times;</span>
    <!-- Contenu du module complet ici -->
  </div>
</div>
</body>
</html>
