import { Component, Input, Output, EventEmitter } from '@angular/core';

interface Question {
  id: string;
  text: string;
  options: string[];
  selectedOptionIndex?: number;  // Ajouté pour suivre l'option sélectionnée
}

@Component({
  selector: 'app-question',
  templateUrl: './question.component.html',
  styleUrls: ['./question.component.css']
})
export class QuestionComponent {
  @Input() question!: Question;
  @Output() optionSelected = new EventEmitter<{questionId: string, selectedOptionIndex: number}>();

  onOptionSelected(optionIndex: number): void {
    this.optionSelected.emit({questionId: this.question.id, selectedOptionIndex: optionIndex});
  }
}
