import { Component } from '@angular/core';
import { ServiceModuleService } from 'src/app/services/service-module.service';
import { ChangeDetectorRef } from '@angular/core';






@Component({
  selector: 'app-module-list',
  templateUrl: './module-list.component.html',
  styleUrls: ['./module-list.component.css']
})
export class ModuleListComponent {
  list: any[] = []; // Assuming 'list' will hold the modules fetched from the backend
  modalDisplay = false;
  currentModule: any = null;
  modalMode: 'edit' | 'view' = 'view'; // Used to differentiate between display and edit in the modal

  constructor(private cdRef: ChangeDetectorRef, private serviceModule: ServiceModuleService) {}

  ngOnInit(): void {
    this.getAllModules();
  }

  openModal(module: any, mode: 'edit' | 'view' = 'view'): void {
    this.currentModule = module;
    this.modalMode = mode;
    this.modalDisplay = true;
  }

  closeModal(): void {
    this.modalDisplay = false;
  }

  saveChanges(): void {
    // Add your logic here to save the changes made to the module
    console.log('Changes saved for', this.currentModule);
    this.closeModal();
  }

  confirmDelete(moduleId: string): void {
    const isConfirmed = confirm('Are you sure you want to delete this module?');
    if (isConfirmed) {
      this.deleteModule(moduleId);
    }
  }

 
  deleteModule(moduleId: string): void {
    this.serviceModule.deleteModule(moduleId).subscribe({
      next: () => {
        console.log("Module deleted successfully");
        // Remove the module from the list without re-fetching the entire list
        this.list = this.list.filter(module => module.id !== moduleId);
        // this.cdRef.detectChanges(); // Use if necessary for manual change detection
      },
      error: (error) => {
        console.error("Error deleting module", error);
        // Optionally re-fetch the list if there's an error, to ensure UI consistency
        this.getAllModules();
      }
    });
  }
  
  

  getAllModules(): void {
    this.serviceModule.getModules().subscribe({
      next: (modules) => {
        this.list = modules;
        console.log('List of Modules updated:', this.list);
        this.cdRef.detectChanges(); // Manually trigger change detection
      },
      error: (error) => {
        console.error('Error fetching modules', error);
      }
    });
  }
  


  updateModule(moduleId: string, newData: any): void {
    this.serviceModule.updateModule(moduleId, newData).subscribe({
      next: (response) => {
        console.log('Module Updated successfully', response);
        this.getAllModules(); // Optionally refresh the list to reflect the update
      },
      error: (error) => {
        console.error('There was an error!', error);
      }
    });
  }

  getModuleById(moduleId: string): void {
    this.serviceModule.getModuleById(moduleId).subscribe({
      next: (module) => {
        console.log('Module Details:', module);
      },
      error: (error) => {
        console.error('There was an error!', error);
      }
    });
  }
}
