import { Component } from '@angular/core';
import { QuizService } from 'src/app/QuizService/quiz.service'; 
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-quiz',
  templateUrl: './create-quiz.component.html',
  styleUrls: ['./create-quiz.component.css']
})
export class CreateQuizComponent {
  quiz: any = {
    title: '',
    questions: [],
    createdDate: new Date(), // Assuming handling on the frontend for simplicity
    creator: '', // This should be set based on the logged-in user or similar
    status: 'Draft', // Default status
    maxAttempts: 1,
    showAnswers: false,
    showScore: false,
    passMark: 0,
    scheduleDate: new Date(), // Set a default or use a date picker in the UI
    durationInSeconds: 0,
    randomizeQuestions: false
  };

  constructor(private quizService: QuizService, private router: Router) {}

  addQuestion(): void {
    const newQuestion = { text: '', options: [], correctAnswerIndex: -1 }; // Default empty question
    this.quiz.questions.push(newQuestion);
  }

  saveQuiz(): void {
    // Update or create based on the quiz's presence in the database
    const operation = this.quiz.id ? this.quizService.updateQuiz(this.quiz.id, this.quiz) : this.quizService.createQuiz(this.quiz);
    
    operation.subscribe(response => {
      console.log('Quiz saved successfully', response);
      this.router.navigate(['/quiz-list']);
    }, error => {
      console.error('Error saving quiz', error);
    });
  }

  

}
