import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { QuizService } from 'src/app/QuizService/quiz.service'; 

@Component({
  selector: 'app-attempts',
  templateUrl: './attempts.component.html',
  styleUrls: ['./attempts.component.css']
})
export class A implements OnInit {
  attempt: any = {
    idAttempt: '',
    quizId: '',
    username: '',
    attemptDate: new Date(),
    responses: {}, // Clé: ID de la question, Valeur: réponse donnée
    score: 0 // Calculé à partir des réponses
  };
  quiz: any; // To store the quiz details

  constructor(private route: ActivatedRoute, private router: Router, private quizService: QuizService) {}


}
