import { Component } from '@angular/core';
import { ServiceModuleService } from 'src/app/services/service-module.service'; 

@Component({
  selector: 'app-module',
  templateUrl: './module.component.html',
  styleUrls: ['./module.component.css']
})
export class ModuleComponent {
  
  
  constructor(private serviceModule: ServiceModuleService){}
  module = {
    nameModule: '',
    credit: ''
   
  };
  addModule() : void {
    
      this.serviceModule.createModule(this.module).subscribe(response => {
        console.log('Module Added successfully', response);
      }, error => {
        console.error('There was an error!', error);
      });
    }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css">
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css">
  <style>
    body { margin: 0; font-family: Arial, sans-serif; background: #f0f0f0; }
    .main-block { display: flex; justify-content: center; align-items: center; height: 100vh; }
    .form-block { background: #fff; padding: 30px; border-radius: 5px; box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); }
    .btn-primary { margin-top: 20px; }
  </style>
</head>
<body>
<div class="container">
  <div class="row justify-content-center align-items-center" style="height: 100vh;">
    <div class="col-md-6">
      <div class="form-block">
        <i class="fas fa-pencil-alt mb-3" style="font-size: 50px; color: #26a9e0;"></i>
        <h1 class="mb-4">Add Module</h1>
        <form #AddForm="ngForm" (ngSubmit)="addModule()" class="needs-validation" novalidate>
          <div class="form-group">
            <label for="nameModule">Module Name</label>
            <input type="text" class="form-control" id="nameModule" [(ngModel)]="module.nameModule" name="nameModule" required>
            <div class="invalid-feedback">Module name is required.</div>
          </div>
          <div class="form-group">
            <label for="credit">Credit</label>
            <input type="text" class="form-control" id="credit" [(ngModel)]="module.credit" name="credit" required pattern="\d*">
            <div class="invalid-feedback">Credit is required and must be a number.</div>
          </div>
          <button type="submit" class="btn btn-primary" [disabled]="!AddForm.form.valid">Add Module</button>
        </form>
      </div>
    </div>
  </div>
</div>
<script>
  // JavaScript pour Bootstrap validation
  (function() {
    'use strict';
    window.addEventListener('load', function() {
      var forms = document.getElementsByClassName('needs-validation');
      var validation = Array.prototype.filter.call(forms, function(form) {
        form.addEventListener('submit', function(event) {
          if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
          }
          form.classList.add('was-validated');
        }, false);
      });
    }, false);
  })();
</script>
</body>
</html>
