import { Component, ViewChild } from '@angular/core';
import { QuizService } from 'src/app/QuizService/quiz.service'; 
import { Router } from '@angular/router';
import { QuestionComponent } from 'src/app/Questions/question/question.component';

@Component({
  selector: 'app-create-quiz',
  templateUrl: './create-quiz.component.html',
  styleUrls: ['./create-quiz.component.css']
})
export class CreateQuizComponent {
  @ViewChild(QuestionComponent) questionComponent!: QuestionComponent;


  quiz: any = {
    title: '',
    questions: [],
    createdDate: new Date(),
    creator: '',
    status: 'Draft',
    maxAttempts: 1,
    showAnswers: false,
    showScore: false,
    passMark: 0,
    scheduleDate: new Date(),
    durationInSeconds: 0,
    randomizeQuestions: false
  };

  constructor(private quizService: QuizService, private router: Router) {}

  addQuestion(): void {
    const newQuestion = {
      text: '',
      options: [''],
      correctAnswerIndex: null
    };
    this.quiz.questions.push(newQuestion);
  }
  addQuestionToQuiz(question: any): void {
    this.quiz.questions.push(question);
  }

  addOption(questionIndex: number): void {
    this.quiz.questions[questionIndex].options.push('');
  }

  saveQuiz(): void {
    this.quizService.createQuiz(this.quiz).subscribe(response => {
      console.log('Quiz saved successfully', response);
      this.router.navigate(['/quiz-list']);
    }, error => {
      console.error('Error saving quiz', error);
    });
  }

}



<div class="quiz-management-container">
    <h1>Create or Edit Quiz</h1>
    <form #quizForm="ngForm" (ngSubmit)="saveQuiz()">
  
      <!-- Quiz Title -->
      <div class="form-group">
        <label for="title">Quiz Title:</label>
        <input type="text" id="title" class="form-control" [(ngModel)]="quiz.title" name="title" required>
      </div>
  
      <app-question (questionAdded)="addQuestionToQuiz($event)"></app-question>
      <!-- Other Quiz Properties -->
      <!-- Quiz Creator -->
      <div class="form-group">
        <label for="creator">Creator:</label>
        <input type="text" id="creator" class="form-control" [(ngModel)]="quiz.creator" name="creator" required>
      </div>
  
      <!-- Quiz Status -->
      <div class="form-group">
        <label for="status">Status:</label>
        <select id="status" class="form-control" [(ngModel)]="quiz.status" name="status" required>
          <option value="Draft">Draft</option>
          <option value="Published">Published</option>
        </select>
      </div>
  
      <!-- Max Attempts -->
      <div class="form-group">
        <label for="maxAttempts">Max Attempts:</label>
        <input type="number" id="maxAttempts" class="form-control" [(ngModel)]="quiz.maxAttempts" name="maxAttempts" required>
      </div>
  
      <!-- Show Answers Checkbox -->
      <div class="form-check">
        <label for="showAnswers" class="form-check-label">
          <input type="checkbox" id="showAnswers" class="form-check-input" [(ngModel)]="quiz.showAnswers" name="showAnswers">
          Show Answers
        </label>
      </div>
  
      <!-- Show Score Checkbox -->
      <div class="form-check">
        <label for="showScore" class="form-check-label">
          <input type="checkbox" id="showScore" class="form-check-input" [(ngModel)]="quiz.showScore" name="showScore">
          Show Score
        </label>
      </div>
  
      <!-- Pass Mark -->
      <div class="form-group">
        <label for="passMark">Pass Mark:</label>
        <input type="number" id="passMark" class="form-control" [(ngModel)]="quiz.passMark" name="passMark" required>
      </div>
  
      <!-- Schedule Date -->
      <div class="form-group">
        <label for="scheduleDate">Schedule Date:</label>
        <input type="datetime-local" id="scheduleDate" class="form-control" [(ngModel)]="quiz.scheduleDate" name="scheduleDate">
      </div>
  
      <!-- Duration in Seconds -->
      <div class="form-group">
        <label for="durationInSeconds">Duration (in seconds):</label>
        <input type="number" id="durationInSeconds" class="form-control" [(ngModel)]="quiz.durationInSeconds" name="durationInSeconds">
      </div>
  
      <!-- Randomize Questions Checkbox -->
      <div class="form-check">
        <label for="randomizeQuestions" class="form-check-label">
          <input type="checkbox" id="randomizeQuestions" class="form-check-input" [(ngModel)]="quiz.randomizeQuestions" name="randomizeQuestions">
          Randomize Questions
        </label>
      </div>
  
      <button type="submit" class="btn btn-primary">Save Quiz</button>
    </form>
</div>
