import { Component } from '@angular/core';
import { ServiceModuleService } from 'src/app/services/service-module.service'; 


@Component({
  selector: 'app-module',
  templateUrl: './module.component.html',
  styleUrls: ['./module.component.css']
})
export class ModuleComponent {
  
  
  constructor(private serviceModule: ServiceModuleService){}
  module = {
    nameModule: '',
    credit: ''
   
  };
  addModule() : void {
    
      this.serviceModule.createModule(this.module).subscribe(response => {
        console.log('Module Added successfully', response);
      }, error => {
        console.error('There was an error!', error);
      });
    }

    

    

    

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css">
  <style>
    body { margin: 0; font-family: Arial, sans-serif; background: #f0f0f0; }
    .main-block { display: flex; justify-content: center; align-items: center; height: 100vh; perspective: 1000px; }
    .form-block { background: #fff; padding: 30px; border-radius: 5px; box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); transition: transform 0.3s; }
    .form-block:hover { transform: translateZ(-50px); }
    .form-block i, h1, p { color: #26a9e0; }
    .form-block i { font-size: 50px; }
    h1 { margin: 10px 0; font-size: 25px; }
    p { font-size: 15px; }
    .btn-primary { padding: 10px 5px; margin-top: 20px; border-radius: 5px; border: none; background: #26a9e0; text-decoration: none; font-size: 15px; font-weight: 400; color: #fff; }
    .btn-primary:hover { background: #85d6de; }
    .form-group { display: flex; flex-direction: column; margin-bottom: 20px; }
    .form-group label { font-weight: bold; margin-bottom: 5px; }
    .form-group input { padding: 5px; border: 1px solid #ccc; border-radius: 5px; margin-bottom: 10px; }
    @media (min-width: 568px) {
      .main-block { flex-direction: row; height: calc(100% - 50px); }
      .form-block { flex: 1; height: auto; }
    }
  </style>
</head>
<body>
<div class="main-block">
  <div class="form-block">
    <i class="fas fa-pencil-alt"></i>
    <h1>Add Module</h1>
    <form #AddForm="ngForm" (ngSubmit)="addModule()">
      <div class="form-group">
        <label for="nameModule">Module Name</label>
        <input type="text" class="form-control" id="nameModule" [(ngModel)]="module.nameModule" name="nameModule" required>
      </div>
      <div class="form-group">
        <label for="credit">Credit</label>
        <input type="text" class="form-control" id="credit" [(ngModel)]="module.credit" name="credit" required>
      </div>
      <button type="submit" class="btn btn-primary" [disabled]="!AddForm.form.valid">Add Module</button>
    </form>
    
  </div>
</div>
</body>
</html>
