import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { QuizService } from 'src/app/QuizService/quiz.service'; 

@Component({
  selector: 'app-attempts',
  templateUrl: './attempts.component.html',
  styleUrls: ['./attempts.component.css']
})
export class AttemptComponent implements OnInit {
  attempt: any = {
    idAttempt: '',
    quizId: '',
    username: '',
    attemptDate: new Date(),
    responses: {}, // Clé: ID de la question, Valeur: réponse donnée
    score: 0 // Calculé à partir des réponses
  };
  quiz: any; // To store the quiz details

}
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { QuizService } from 'src/app/QuizService/quiz.service';

// Inline representation of your backend entities
interface Question {
  id: string;
  text: string;
  options: string[];
}

interface Quiz {
  id: string;
  title: string;
  questions: Question[];
}


export class QuizAttemptComponent implements OnInit {
  quiz: Quiz; // Will hold the quiz data
  studentResponses: { [questionId: string]: number } = {}; // Maps question ID to the student's selected option index
  studentName: string = ''; // To hold the student's name
  feedback: string = ''; // To hold any feedback from the student

  constructor(
    private route: ActivatedRoute, 
    private quizService: QuizService
  ) {}

  ngOnInit(): void {
    const quizId = this.route.snapshot.paramMap.get('id');
    if (quizId) {
      this.quizService.getQuizById(quizId).subscribe(
        (data: Quiz) => {
          this.quiz = data;
          // Initialize responses for each question
          this.quiz.questions.forEach(question => {
            this.studentResponses[question.id] = -1; // -1 means no option selected
          });
        },
        (error) => {
          console.error('Error loading quiz', error);
          // Handle loading error
        }
      );
    } else {
      console.error('Quiz ID not provided');
      // Handle missing quiz ID error
    }
  }

  selectAnswer(questionId: string, optionIndex: number): void {
    this.studentResponses[questionId] = optionIndex;
  }

  submitAttempt(): void {
    const attemptData = {
      quizId: this.quiz.id,
      studentName: this.studentName,
      responses: this.studentResponses,
      feedback: this.feedback
    };
    
    console.log('Submitting the following attempt:', attemptData);
    // Assuming your QuizService has a method to submit the attempt
    this.quizService.submitAttempt(attemptData).subscribe(
      result => {
        console.log('Quiz attempt submitted successfully:', result);
        // Redirect to a confirmation page or display a success message
      },
      error => {
        console.error('Error submitting quiz attempt:', error);
        // Handle submission error
      }
    );
  }
}
