import { Component } from '@angular/core';
import { ServiceModuleService } from 'src/app/services/service-module.service'; 
@Component({
  selector: 'app-module-list',
 
  templateUrl: './module-list.component.html',
  styleUrls: ['./module-list.component.css']
})
export class ModuleListComponent {
  list: any;
  module: { id: number; nameModule: string; credit: string; }[] = [];

 ngOnInit(): void {

  this.getAllModules();
  console.log("ok");
  this.list=this.module;

 }

  constructor(private serviceModule: ServiceModuleService){}
  

  deleteModule(moduleId: number): void {
    this.serviceModule.deleteModule(moduleId).subscribe(response => {
      console.log('Module Deleted successfully', response);
    }, error => {
      console.error('There was an error!', error);
    });
  }

  updateModule(moduleId: number, newData: any): void {
    this.serviceModule.updateModule(moduleId, newData).subscribe(response => {
      console.log('Module Updated successfully', response);
    }, error => {
      console.error('There was an error!', error);
    });
  }

  getAllModules(): void {
    this.serviceModule.getModules().subscribe(modules => {
      this.list=modules;
      console.log('List of Modules:', modules);
      console.log('bravo');
    }, error => {
      console.error('There was an error!', error);
      console.log("erreur msg" || error);
    });

  }

  getModuleById(moduleId: number): void {
    this.serviceModule.getModuleById(moduleId).subscribe(module => {
      console.log('Module Details:', module);
    }, error => {
      console.error('There was an error!', error);
    });
  }



}
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<title>Module Display</title>
<link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css">
<style>
  .card {
    margin-bottom: 20px;
  }
</style>
</head>
<body>

<div class="container">
  <div id="modulesContainer" class="row"></div>
</div>

<script>
  // Supposons que vous ayez une liste similaire à celle-ci
  var list = [
    { id: 1, nameModule: 'Module 1', credit: 5 },
    { id: 2, nameModule: 'Module 2', credit: 4 },
    // Ajoutez d'autres modules ici
  ];

  function showDetails(id) {
    alert("Show details for module ID " + id);
  }

  function editModule(id) {
    alert("Edit module ID " + id);
  }

  function deleteModule(id) {
    alert("Delete module ID " + id);
    // Supprimez l'élément du tableau 'list' et rafraîchissez l'affichage
  }

  function renderModules() {
    var modulesContainer = document.getElementById('modulesContainer');
    modulesContainer.innerHTML = ''; // Clear existing content

    list.forEach(function(m) {
      var cardHtml = `
        <div class="col-md-4">
          <div class="card">
            <div class="card-body">
              <h5 class="card-title">${m.nameModule}</h5>
              <p class="card-text">Crédit: ${m.credit}</p>
              <button onclick="showDetails(${m.id})" class="btn btn-primary">Show</button>
              <button onclick="editModule(${m.id})" class="btn btn-secondary">Edit</button>
              <button onclick="deleteModule(${m.id})" class="btn btn-danger">Delete</button>
            </div>
          </div>
        </div>
      `;
      modulesContainer.innerHTML += cardHtml;
    });
  }

  // Initialiser l'affichage lorsque la page est chargée
  window.onload = function() {
    renderModules();
  };
</script>

</body>
</html>
