import { Component } from '@angular/core';
import { ServiceModuleService } from 'src/app/services/service-module.service'; 
@Component({
  selector: 'app-module-list',
 
  templateUrl: './module-list.component.html',
  styleUrls: ['./module-list.component.css']
})
export class ModuleListComponent {
  list=[]
  module: { id: number; nameModule: string; credit: string; }[] = [];

 ngOnInit(): void {

  this.getAllModules();
  console.log("ok");
 }

  constructor(private serviceModule: ServiceModuleService){}
  

  deleteModule(moduleId: number): void {
    this.serviceModule.deleteModule(moduleId).subscribe(response => {
      console.log('Module Deleted successfully', response);
    }, error => {
      console.error('There was an error!', error);
    });
  }

  updateModule(moduleId: number, newData: any): void {
    this.serviceModule.updateModule(moduleId, newData).subscribe(response => {
      console.log('Module Updated successfully', response);
    }, error => {
      console.error('There was an error!', error);
    });
  }

  getAllModules(): void {
    this.serviceModule.getModules().subscribe(modules => {
      this.list=modules;
      console.log('List of Modules:', modules);
      console.log('bravo');
    }, error => {
      console.error('There was an error!', error);
      console.log("erreur msg" || error);
    });

  }

  getModuleById(moduleId: number): void {
    this.serviceModule.getModuleById(moduleId).subscribe(module => {
      console.log('Module Details:', module);
    }, error => {
      console.error('There was an error!', error);
    });
  }



}
