import { Component } from '@angular/core';

@Component({
  selector: 'app-module',
 
  templateUrl: './module.component.html',
  styleUrls: ['./module.component.css']
})
export class ModuleComponent {

   // Initialize recFormData with the structure expected by your form
   recFormData: any = {
    fullNameCompany: '',
    fullNameManager: '',
    email: '',
    password: '',
    companyPhoneNumber: '',
    university: '',
    levelOfStudy: ''
    // Add other form fields as needed
  };

  // Add your submitForm method if not already present
  submitForm() {
    console.log('Form submitted', this.recFormData);
    // Handle form submission logic here
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css">
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            background: #f0f0f0;
        }

        .main-block {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            perspective: 1000px;
        }

        .left-part,
        form {
            background: #fff;
            padding: 30px;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            transition: transform 0.3s;
        }

        .left-part {
            transform: translateZ(-100px);
        }

        .left-part:hover,
        form:hover {
            transform: translateZ(-50px);
        }

        .left-part i,
        h1,
        p {
            color: #26a9e0;
        }

        .left-part i {
            font-size: 50px;
        }

        h1 {
            margin: 10px 0;
            font-size: 25px;
        }

        p {
            font-size: 15px;
        }

        .btn-group {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            height: 100%;
        }

        .btn-item,
        button {
            padding: 10px 5px;
            margin-top: 20px;
            border-radius: 5px;
            border: none;
            background: #26a9e0;
            text-decoration: none;
            font-size: 15px;
            font-weight: 400;
            color: #fff;
        }

        .btn-item {
            display: block;
            width: 80%;
            text-align: center;
        }

        button {
            width: 100%;
        }

        button:hover,
        .btn-item:hover {
            background: #85d6de;
        }

        form {
            display: flex;
            flex-direction: column;
        }

        .title {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
        }

        .title i {
            margin-right: 10px;
        }

        .info {
            display: flex;
            flex-direction: column;
            margin-bottom: 20px;
        }

        .info label {
            font-weight: bold;
            margin-bottom: 5px;
        }

        .info input,
        .info select {
            padding: 5px;
            border: 1px solid #ccc;
            border-radius: 5px;
            margin-bottom: 10px;
        }

        .agree {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
        }

        .agree input {
            margin-right: 10px;
        }

        .agree a {
            color: #26a9e0;
        }

        .agree a:hover {
            text-decoration: underline;
        }

        input[type="submit"] {
            cursor: pointer;
        }

        @media (min-width: 568px) {
            .main-block {
                flex-direction: row;
                height: calc(100% - 50px);
            }

            .left-part,
            form {
                flex: 1;
                height: auto;
            }

            .left-part {
                transform: translateZ(0);
            }

            .left-part:hover,
            form:hover {
                transform: translateZ(50px);
            }

            .btn-group {
                flex-direction: row;
                justify-content: space-between;
                align-items: center;
                height: auto;
            }

            .btn-item {
                width: 45%;
            }
        }
    </style>
</head>
<body>
<div class="main-block">
    <div class="left-part">
        <i class="fas fa-graduation-cap"></i>
        <h1>REGISTER FOR GETTING RECOMMENDATION</h1>
        <div class="btn-group">
            <!-- Use routerLink for Angular routing -->
            <a class="btn-item" routerLink="/students">VIEW ALL STUDENTS</a>
            <a class="btn-item" href="#">VIEW ALL UNIVERSITIES</a>
        </div>
    </div>
    <form (ngSubmit)="submitForm()" #recForm="ngForm">
        
        <div class="title">
            <i class="fas fa-pencil-alt"></i>
            <h2>Register here</h2>
        </div>
        <div class="info">
            <input class="fname" type="text" name="fullNameCompany" [(ngModel)]="recFormData.fullNameCompany" required placeholder="Full name Of Company">
            <input class="fname" type="text" name="fullNameManager" [(ngModel)]="recFormData.fullNameManager" required placeholder="Full name Of Manager">
            <input type="email" name="email" [(ngModel)]="recFormData.email" required placeholder="Email">
            <input type="password" name="password" [(ngModel)]="recFormData.password" required placeholder="Password">
            <input type="text" name="companyPhoneNumber" [(ngModel)]="recFormData.companyPhoneNumber" required placeholder="Company Phone Number">
            <select name="university" [(ngModel)]="recFormData.university" required>
                <option value="" selected disabled>Select University</option>
                <option value="iset rades">iset rades</option>
                <option value="iset sousse">iset sousse</option>
                <option value="insat">insat</option>
                <option value="esprit">esprit ecoles sup des ingenieurs</option>
            </select>
            
            <select name="levelOfStudy" [(ngModel)]="recFormData.levelOfStudy" required>
                <option value="" selected disabled>Select student's level of study preference</option>
                <option value="Diploma">Diploma</option>
                <option value="certificate">Certificate</option>
                <option value="masters-degree">Masters degree</option>
                <option value="postgraduate">Postgraduate</option>
            </select>
        </div>
        <div class="agree">
            <input type="checkbox" name="agreeToPrivacyPolicy" required>
            <span>I agree to the <a href="https://www.w3docs.com/privacy-policy">Privacy Policy for W3Docs.</a></span>
        </div>
        <button type="submit" [disabled]="!recForm.form.valid">Submit</button>
    </form>
</div>
</body>
</html>
